import { axisBottom, axisLeft } from 'd3-axis';
import Utilities from './Utilities';

/**
* The Axis object is used to instantiate x and y axes, as well as labels
*
* @class Axis
* @constructor
*/
export default class Axis {

  /**
  * d3 object for axis container
  *
  * @property d3Container
  * @type {Object}
  */
  d3Container;

  /**
  * Amount to truncate axis labels to
  *
  * @property truncateSize
  * @type {Object}
  */
  truncateSize;

  /**
  * Collection of axis labels
  *
  * @property axisLabels
  * @type {Array}
  */
  axisLabels;

  /**
  * Scale object for the x axis
  *
  * @property scaleX
  * @type {Object}
  */
  scaleX;

  /**
  * Scale object for the y axis
  *
  * @property scaleY
  * @type {Object}
  */
  scaleY;

  /**
  * Chart's tooltip object
  *
  * @property tooltip
  * @type {Object}
  */
  tooltip;

  /**
  * The current calculated width of the chart
  *
  * @property width
  * @type {Number}
  */
  width;

  /**
  * The current calculated height of the chart
  *
  * @property height
  * @type {Number}
  */
  height;

  /**
  * The padding for the chart within the container
  *
  * @property padding
  * @type {Object}
  */
  padding;

  /**
  * Constructor function which sets up the local object.
  *
  * @method constructor
  * @param {Object} chartParams configuration parameters object
  * @throws {Error} invalid parameters
  */
  constructor(chartParams) {
    if (chartParams.d3Container) {
      Object.assign(this, chartParams);
    } else {
      throw new Error('Incorrect parameters provided to Axis constructor.');
    }
  }

  /**
  * Master render to call all rendering methods
  *
  * @method render
  * @chainable
  */
  render() {
    this.renderAxisX();
    this.renderAxisY();
    this.renderLabels();
    return this;
  }

  /**
  * Render only the x axis
  *
  * @method renderAxisX
  */
  renderAxisX() {
    this.d3Container.selectAll('g.x-axis').remove();
    this.d3Container.append('g')
        .attr('class', 'x-axis')
        .call(axisBottom(this.scaleX))
        .attr('transform', `translate(${this.padding.l},${this.height})`)
        .selectAll('text')
          .attr('x', -5)
          .attr('y', 6)
          .attr('transform', 'rotate(310)')
          .attr('class', 'x-labels')
          .text(d => Utilities.truncateString(d, this.truncateSize))
          .style('text-anchor', 'end')
          .on('mousemove', (d) => {
            if (this.tooltip && d.length > this.truncateSize) {
              this.tooltip.ping(`<strong>${d}</strong>`);
            }
          });
  }

  /**
  * Render only the y axis
  *
  * @method renderAxisY
  */
  renderAxisY() {
    this.d3Container.selectAll('g.y-axis').remove();
    this.d3Container.append('g')
        .attr('class', 'y-axis')
        .call(axisLeft(this.scaleY))
        .attr('transform', `translate(${this.padding.l},0)`)
        .selectAll('.y-axis .tick line')
          .attr('x2', () => this.width);
  }

  /**
  * Render only the labels
  *
  * @method renderLabels
  */
  renderLabels() {
    this.d3Container.selectAll('text.labels').remove();
    this.d3Container.append('text')
        .attr('class', 'labels')
        .attr('x', (this.height / -2) - (this.padding.t / 2))// - ((this.chartConfig.axisLabels[0].length / 2) * 7))
        .attr('y', 10)
        .attr('transform', 'rotate(-90)')
        .attr('text-anchor', 'middle')
        .text(this.axisLabels[0]);
    this.d3Container.append('text')
        .attr('class', 'labels')
        .attr('x', ((this.width + this.padding.l + this.padding.r) / 2))// - ((this.axisLabels[1].length / 2) * 2))
        .attr('y', this.height + (this.padding.b - 5))
        .attr('text-anchor', 'middle')
        .text(this.axisLabels[1]);
  }

}

module.exports = Axis;

import { min, max } from 'd3-array';
import { select } from 'd3-selection';
import { scaleLinear, scaleBand } from 'd3-scale';
import Chart from './Chart';
import Key from './Key';
import Axis from './Axis';

/**
* Create AxisChart from the supplied data, based on the supplied JSON config.
*
* @class AxisChart
* @extends {Chart}
* @constructor
*/
export default class AxisChart extends Chart {

  /**
  * The d3 object for the chart's group
  *
  * @property d3ChartGroup
  * @type {Object}
  */
  d3ChartGroup;

  /**
  * The d3 object for the chart's axis
  *
  * @property axisGroup
  * @type {Object}
  */
  axisGroup;

  /**
  * The chart's axis object
  *
  * @property axis
  * @type {Object}
  */
  axis;

  /**
  * The chart's key object
  *
  * @property key
  * @type {Object}
  */
  key;

  /**
  * Constructor function supersedes parent class.
  *
  * @method constructor
  * @param {Object} chartParams same as Chart
  */
  constructor(chartParams = {}) {
    super(chartParams);
    this.padding = { l: 45, r: 15, t: 25, b: 90 };
    this.scaleY = scaleLinear();
    this.scaleX = scaleBand().padding(0.2);
  }

  /**
  * Supersede the parent method to update local scaling objects
  *
  * @method setDimensions
  */
  setDimensions() {
    super.setDimensions();
    const doTrim = this.chartConfig.doTrim;
    let minValue = doTrim ? min(this.chartData, d => min(d.itemValues)) : 0;
    let maxValue = max(this.chartData, d => max(d.itemValues));
    const dataSlice = Math.ceil(maxValue / 15);
    if (doTrim) {
      const iLowerSection = minValue > dataSlice ? minValue - dataSlice : 0;
      minValue = minValue > 0 ? iLowerSection : minValue;
      minValue = minValue < 0 ? minValue - dataSlice : minValue;
      maxValue += dataSlice;
    }
    this.scaleX
      .domain(this.chartData.map(d => d.itemLabel))
      .range([0, this.innerWidth]);
    this.scaleY
      .domain([minValue, maxValue])
      .range([this.innerHeight, this.padding.t]);
    this.minValue = minValue;
    this.maxValue = maxValue;
    this.dataSlice = dataSlice;
  }

  /**
  * Render the chart including axes and labels
  *
  * @method renderChart
  */
  renderChart() {
    const { axisLabels, truncateSize = 15 } = this.chartConfig;
    const { innerWidth, innerHeight, scaleX, scaleY, padding } = this;

    // Add chart scale axes
    this.axisGroup = this.axisGroup || this.d3Svg.append('g').attr('class', 'axes-g');
    this.axis = new Axis({
      d3Container: this.axisGroup,
      truncateSize,
      axisLabels,
      scaleX,
      scaleY,
      padding,
      width: innerWidth,
      height: innerHeight,
      tooltip: this.tooltip }).render();

    // Add chart container group
    this.d3ChartGroup = this.d3ChartGroup || this.d3Svg.append('g').attr('transform', `translate(${this.padding.l}, 0)`);

    // Render the key for the data
    this.key = new Key({
      d3Container: select(this.svg),
      itemValues: this.chartConfig.itemValues,
      offsetX: (this.innerWidth / 2) + this.padding.l,
      offsetY: this.height - 20
    }).render();
  }

}

module.exports = AxisChart;

import { easeLinear } from 'd3-ease';
import { select, event } from 'd3-selection';
import 'd3-transition';
import AxisChart from './AxisChart';

/**
* Create BarCharts from the supplied data, based on the JSON config.
*
* @class BarChart
* @extends {AxisChart}
* @constructor
*/
export default class BarChart extends AxisChart {

  /**
  * The local collection of bars
  *
  * @property bars
  * @type {Array}
  */
  bars;

  /**
  * Constructor used to set chart type
  *
  * @method constructor
  */
  constructor(chartParams = {}) {
    super(chartParams);
    this.chartType = 'bar';
  }

  /**
  * Render the chart including bars, axes and labels
  *
  * @method renderChart
  * @param {Boolean} doReset optionally reset the chart data
  * @param {Boolean} doHeightTransition optionally transition height
  */
  renderChart(doReset = false, doHeightTransition = true) {
    super.renderChart();
    const { itemValues, sBarType = 'side' } = this.chartConfig;
    const { innerHeight, scaleX, scaleY } = this;
    const barWidth = scaleX.bandwidth() / itemValues.length;

    this.bars = this.bars || [];

    // Iterate through config value keys
    itemValues.forEach((oValues, i) => {
      const { color } = oValues;
      const iBarOffset = sBarType === 'side' ? (barWidth * i) : 0;
      // Reset bars data and clear graph
      if (doReset && this.bars[i]) {
        this.bars[i] = this.d3ChartGroup.selectAll(`rect.bars-${i}`).data({});
        this.bars[i].exit().remove();
        this.bars[i] = undefined;
      }
      // Add bars for each value
      if (!this.bars[i]) {
        // Bind bars data
        this.bars[i] = this.d3ChartGroup.selectAll(`rect.bars-${i}`).data(this.chartData);
        // Add new rect elements and set base attributes
        this.bars[i]
          .enter()
          .append('rect')
          .on('mousemove', (d) => {
            this.tooltip.ping([d.itemLabel, oValues.name, d.itemValues[i]]);
          })
          .on('mouseover', (d) => {
            d.color = color;
            select(event.target).attr('fill', d.color.darker(1));
          })
          .on('mousedown', (d) => {
            if (this.chartConfig.fnClickCallback) {
              this.chartConfig.fnClickCallback({
                oEvent: event,
                jData: d
              });
            }
          })
          .on('mouseout', (d) => {
            this.tooltip.hide();
            select(event.target).attr('fill', d.color);
          })
          .attr('class', `bars bars-${i}`)
          .attr('fill', color)
          .attr('y', innerHeight)
          .attr('height', 0);
      }

      // Update bars (IIFE used to allow for optional transition)
      (() => {
        const d3BarGroup = this.d3ChartGroup.selectAll(`rect.bars-${i}`);
        d3BarGroup.data(this.chartData)
          .attr('x', d => scaleX(d.itemLabel) + iBarOffset)
          .attr('width', barWidth);
        if (doHeightTransition) {
          return d3BarGroup.transition(false)
          .ease(easeLinear)
          .duration(this.transitionTime);
        }
        return d3BarGroup;
      })().attr('y', (d) => {
        let value = d.itemValues[i];
        value = value < 0 ? Math.abs(value) : 0;
        return scaleY(d.itemValues[i] + value);
      })
      .attr('height', (d) => {
        const modifier = this.minValue < 0 ? Math.abs(this.minValue) : 0;
        return innerHeight - scaleY(Math.abs(d.itemValues[i]) - modifier);
      });
    });
  }

}

module.exports = BarChart;

import { forceSimulation, forceManyBody, forceCenter } from 'd3-force';
import { max } from 'd3-array';
import { select } from 'd3-selection';
import { easeLinear } from 'd3-ease';
import { scaleLinear } from 'd3-scale';
import Chart from './Chart';
import Axis from './Axis';
import Key from './Key';

/**
* Create BubbleCharts from the supplied data, based on the supplied JSON config.
*
* @class BubbleChart
* @extends {Chart}
* @constructor
*/
export default class BubbleChart extends Chart {

  /**
  * The local collection of bubbles
  *
  * @property bubbles
  * @type {Array}
  */
  bubbles;

  /**
  * Constructor function supersedes parent class.
  *
  * @method constructor
  * @param {Object} chartParams same as Chart
  */
  constructor(chartParams = {}) {
    super(chartParams);
    this.scaleColor = scaleLinear();
    this.chartType = 'bubble';
  }

  /**
  * Supersede the parent method to update local scaling objects
  *
  * @method setDimensions
  */
  setDimensions() {
    super.setDimensions();
    this.scaleColor
      .domain([0, max(this.chartData, d => d[this.chartConfig.itemValues[1].key])])
      .range(this.chartConfig.itemValues[1].colors);
  }

  /**
  * Render the chart including bubbles, axes and labels
  *
  * @method renderChart
  */
  renderChart() {
    const { axisLabels, itemValues } = this.chartConfig;
    const { innerWidth, innerHeight, padding } = this;
    const { key: key1, name: name1 } = itemValues[0];
    const { key: key2, name: name2, colors } = itemValues[1];

    // Add chart scale axes
    this.axisGroup = this.axisGroup || this.d3Svg.append('g').attr('class', 'axes-g');
    this.axis = new Axis({
      d3Container: this.axisGroup,
      axisLabels,
      padding,
      width: innerWidth,
      height: innerHeight - 25 }).renderLabels();

    // Add the group container for bubbles
    this.bubblesGroup = this.bubblesGroup || this.d3Svg.append('g').attr('transform', `translate(${this.padding.l}, 0)`);

    // The method runs on each tick of the force calculation to reposition the bubbles
    const fnTicked = () => {
      this.bubblesGroup.selectAll('circle')
        .attr('cx', d => d.x)
        .attr('cy', d => d.y);
    };

    // Initialise the d3 force calculations
    this.force = forceSimulation()
      .nodes(JSON.parse(JSON.stringify(this.chartData)))
      .force('charge', forceManyBody().strength(d => (d[key1] * (d[key1] / 2)) / -150))
      .force('center', forceCenter(this.width / 2, this.height / 2))
      .on('tick', () => {
        fnTicked();
      });

    // Add circles for each value
    if (!this.circles) {
      // Bind bars data
      this.circles = this.bubblesGroup.selectAll('circle.circles').data(this.force.nodes());
      // Add new rect elements and set base attributes
      this.circles
        .enter()
        .append('circle')
        .on('mousemove', (d) => {
          this.tooltip.ping(`<strong>${d.itemLabel}</strong><br>${name1}: <em>${d[key1]}</em><br>${name2}: <em>${d[key2]}</em>`);
        })
        .on('mouseout', () => this.tooltip.hide())
        .attr('class', 'circles')
        .attr('fill', d => this.scaleColor(d[key2]))
        .attr('r', 0)
        .transition()
        .ease(easeLinear)
        .duration(this.transitionTime)
        .attr('r', d => d[key1] / 2);
    } else {
      this.bubblesGroup.attr('transform', `translate(${this.resizeOffset / 2}, 0)`);
    }

    // Render the key for the data
    this.key = new Key({
      d3Container: select(this.svg),
      itemValues: [
        {
          name: 0,
          color: colors[0]
        },
        {
          name: this.scaleColor.domain()[1],
          color: colors[1]
        }
      ],
      offsetX: (this.innerWidth / 2) + this.padding.l + 10,
      offsetY: this.height - 20,
      chartType: 'range'
    }).render();
  }

}

module.exports = BubbleChart;

import { select } from 'd3-selection';
import Tooltip from './Tooltip';
import DataOps from './DataOps';

/**
* The Chart object is the parent class for all types of Chart.
* It is used to initialise all of the base settings universal to all charts.
*
* @class Chart
* @constructor
*/
export default class Chart {

  /**
  * SVG DOM object for displaying the chart
  *
  * @property svg
  * @type {Object}
  */
  svg;

  /**
  * SVG d3 object for d3 operations on the chart
  *
  * @property d3Svg
  * @type {Object}
  */
  d3Svg;

  /**
  * Default time for d3 transitions on the chart
  *
  * @property transitionTime
  * @type {Number}
  */
  transitionTime;

  /**
  * DOM reference to container element that wraps SVG
  *
  * @property container
  * @type {Object}
  */
  container;

  /**
  * DOM reference to loader display element
  *
  * @property loader
  * @type {Object}
  */
  loader;

  /**
  * Chart's tooltip object
  *
  * @property tooltip
  * @type {Object}
  */
  tooltip;

  /**
  * The current calculated width of the chart
  *
  * @property width
  * @type {Number}
  */
  width;

  /**
  * The current calculated height of the chart
  *
  * @property height
  * @type {Number}
  */
  height;

  /**
  * The current calculated inner width of the chart
  *
  * @property innerWidth
  * @type {Number}
  */
  innerWidth;

  /**
  * The current calculated inner height of the chart
  *
  * @property innerHeight
  * @type {Number}
  */
  innerHeight;

  /**
  * The width before any browser resize
  *
  * @property initialWidth
  * @type {Number}
  */
  initialWidth;

  /**
  * The padding for the chart within the container
  *
  * @property padding
  * @type {Object}
  */
  padding;

  /**
  * The chart's config object
  *
  * @property chartConfig
  * @type {Object}
  */
  chartConfig;

  /**
  * The chart's data
  *
  * @property chartData
  * @type {Array}
  */
  chartData;

  /**
  * Constructor function that sets up the local object.
  *
  * @method constructor
  * @param {Object} chartConfig JSON configuration object
  * @param {Array} chartData the data to be displayed
  * @param {String} containerId Optional ID to select DOM object
  * @param {Object} container Optional DOM object in place of ID
  */
  constructor(chartParams = {}) {
    const { chartConfig, chartData, containerId } = chartParams;
    let { container } = chartParams;
    this.svg = document.createElementNS('http://www.w3.org/2000/svg', 'svg');
    this.d3Svg = select(this.svg);
    this.transitionTime = 500;
    this.padding = { l: 5, r: 5, t: 5, b: 5 };
    this.loader = document.createElement('div');
    this.loader.className = 'dt-loader';
    if (!container && containerId) {
      container = document.getElementById(containerId);
    }
    if (container) {
      this.setContainer(container);
    }
    if (chartConfig) {
      this.setConfig(chartConfig);
    }
    if (chartData) {
      this.setData(chartData);
    }
  }

  /**
  * Sets the local container object.
  *
  * @method setContainer
  * @param {Object} container Required DOM element
  * @throws {Error} invalid DOM element
  */
  setContainer(container) {
    if (container.nodeName) {
      container.appendChild(this.loader);
      this.container = container;
    } else {
      throw new Error('No valid DOM element or ID provided for chart.');
    }
  }

  /**
  * Sets the local config options for the chart.
  *
  * @method setConfig
  * @param {Object} chartConfig JSON configuration object
  * @throws {Error} missing configuration
  */
  setConfig(chartConfig) {
    if (chartConfig && chartConfig.toString() === '[object Object]') {
      if (chartConfig.itemValues) {
        chartConfig.itemValues = DataOps.addColoursToConfig(chartConfig.itemValues);
      }
      this.chartConfig = chartConfig;
    } else {
      throw new Error('No valid configuration provided for chart.');
    }
  }

  /**
  * Sets the local data for the chart.
  *
  * @method setData
  * @param {Array} chartData array of JSON objects
  * @param {Boolean} doTransform transform mapped data
  * @throws {Error} missing data
  */
  setData(chartData, doTransform = true) {
    if (chartData && Array.isArray(chartData) === true) {
      if (this.chartConfig && doTransform) {
        chartData = DataOps.transformDataKeys(this.chartConfig, chartData);
      }
      this.chartData = chartData;
    } else {
      throw new Error('No valid data provided for chart.');
    }
  }

  /**
  * Updates the local data for the chart.
  *
  * @method updateData
  * @param {Array} chartData array of JSON objects
  * @param {Boolean} doTransform transform mapped data
  */
  updateData(chartData, doTransform = true) {
    this.setData(chartData, doTransform);
    this.setDimensions();
    if (this.axis) {
      this.axis.render();
    }
    if (this.renderChart) {
      this.renderChart();
    }
  }

  /**
  * Updates the local config for the chart.
  *
  * @method updateConfig
  * @param {JSON} chartConfig config JSON style object
  */
  updateConfig(chartConfig) {
    this.setConfig(chartConfig);
    if (this.renderChart) {
      this.renderChart(true, false);
    }
  }

  /**
  * Sets the local chart dimensions based on the size of the container.
  *
  * @method setDimensions
  * @throws {Error} missing DOM element
  */
  setDimensions() {
    if (this.container && this.container.nodeName) {
      this.width = this.container.clientWidth;
      this.height = this.container.clientHeight;
      this.innerWidth = this.width - this.padding.l - this.padding.r;
      this.innerHeight = this.height - this.padding.t - this.padding.b;
    } else {
      throw new Error('Cannot set dimensions of chart without container element.');
    }
  }

  /**
  * Check chart is ready and render.
  *
  * @method init
  * @throws {Error} chart not ready for initialisation
  * @chainable
  */
  init() {
    this.setDimensions();
    if (this.chartData && this.chartConfig && !isNaN(this.width) && !isNaN(this.height)) {
      this.initialWidth = this.width;
      this.tooltip = new Tooltip(this.container).create();
      select(this.container).append('div').attr('class', 'title').text(this.chartConfig.title);
      this.svg.setAttribute('class', 'chart');
      this.container.appendChild(this.svg);
      this.resizeWatcher = this.resizeWatcher || window.addEventListener('resize', () => {
        this.setDimensions();
        this.resizeOffset = this.width - this.initialWidth;
        if (this.renderChart) {
          this.renderChart();
        }
        this.tooltip.hide();
      });
      this.chartOutWatcher = this.chartOutWatcher || this.svg.addEventListener('mouseout', () => {
        this.tooltip.hide();
      });
      if (this.renderChart) {
        this.renderChart();
      }
      this.container.removeChild(this.loader);
      return this;
    }
    throw new Error('The chart is not ready for initialisation.');
  }

}

module.exports = Chart;

import { rgb } from 'd3-color';
import Utilities from './Utilities';

/**
* The Colors object is is a class containing reusable static methods
*
* @class Colors
* @static
*/
export default class Colors {

  /**
  * Returns a random color (D3 object)
  *
  * @method getRandomColor
  * @static
  * @param {String} contrast the type of background light/dark to filter for contrast
  * @param {Integer} boundary the brightness value to filter
  * @return {Object} randomly generated RGB object
  */
  static getRandomColor(contrast = 'light', boundary = 170) {
    const iRGB = () => Utilities.getRandomInteger(0, 255);
    let itemValues = [];
    while (!itemValues.length || !this.colorFilter(contrast, boundary, itemValues)) {
      itemValues = [iRGB(), iRGB(), iRGB()];
    }
    return rgb(itemValues[0], itemValues[1], itemValues[2]);
  }

  /**
  * Used to filter random colour values and ensure high contrast against background
  *
  * @method colorFilter
  * @static
  * @param {String} contrast the type of background light/dark to filter for contrast
  * @param {Integer} boundary the brightness value to filter
  * @param {Array} itemValues array of RGB values
  * @return {Boolean} specify whether the values pass or fail
  */
  static colorFilter(contrast, boundary, itemValues) {
    let isPass = false;
    if (!itemValues.length) {
      isPass = false;
    } else if (!contrast) {
      isPass = true
    } else {
      const brightness = ((itemValues[0] * 299) + (itemValues[1] * 587) + (itemValues[2] * 114)) / 1000;
      if (contrast === 'light') {
        isPass = brightness < boundary;
      } else if (contrast === 'dark') {
        isPass = brightness > boundary;
      }
    }
    return isPass;
  }

  /**
  * Converts a HEX string to an RGB value
  *
  * @method convertHexToRgb
  * @static
  * @param {String} color the HEX colour
  * @return {Object} RGB color object
  */
  static convertHexToRgb(color) {
    return rgb(color);
  }

  /**
  * Returns a random color palette
  *
  * @method getRandomPalette
  * @static
  * @param {Integer} length the size of the palette
