  * @method truncateString
  * @static
  * @param {String} value string to be truncated
  * @param {Integer} maxLength max length of string before truncation
  * @return {String} truncated string
  */
  static truncateString(value, maxLength) {
    if (value.length > maxLength) {
      return `${value.slice(0, maxLength - 3)}[...]`;
    }
    return value;
  }

  /**
  * Generates a random integer within a range.
  *
  * @method getRandomInteger
  * @static
  * @param {Integer} minValue minimum random value
  * @param {Integer} maxValue max random value
  * @param {Integer} omitValue specify a value to omit from results
  * @return {Integer} new random integer
  */
  static getRandomInteger(minValue, maxValue, omitValue) {
    let iInt = Math.floor(Math.random() * (minValue - maxValue - 1)) + maxValue + 1;
    if (omitValue) {
      while (iInt === omitValue) {
        iInt = this.getRandomInteger(minValue, maxValue);
      }
    }
