import { axisBottom, axisLeft } from 'd3-axis';
import Utilities from './Utilities';

/**
* The Axis object is used to instantiate x and y axes, as well as labels
*
* @class Axis
* @constructor
*/
export default class Axis {

  /**
  * d3 object for axis container
  *
  * @property d3Container
  * @type {Object}
  */
  d3Container;

  /**
  * Amount to truncate axis labels to
  *
  * @property iTruncate
  * @type {Object}
  */
  iTruncate;

  /**
  * Collection of axis labels
  *
  * @property aAxisLabels
  * @type {Array}
  */
  aAxisLabels;

  /**
  * Scale object for the x axis
  *
  * @property oScaleX
  * @type {Object}
  */
  oScaleX;

  /**
  * Scale object for the y axis
  *
  * @property oScaleY
  * @type {Object}
  */
  oScaleY;

  /**
  * Chart's tooltip object
  *
  * @property oTooltip
  * @type {Object}
  */
  oTooltip;

  /**
  * The current calculated width of the chart
  *
  * @property iWidth
  * @type {Number}
  */
  iWidth;

  /**
  * The current calculated height of the chart
  *
  * @property iHeight
  * @type {Number}
  */
  iHeight;

  /**
  * The padding for the chart within the container
  *
  * @property jPadding
  * @type {Object}
  */
  jPadding;

  /**
  * Constructor function which sets up the local object.
  *
  * @method constructor
  * @param {Object} oParams configuration parameters object
  * @throws {Error} invalid parameters
  */
  constructor(oParams) {
    if (oParams.d3Container) {
      Object.assign(this, oParams);
    } else {
      throw new Error('Incorrect parameters provided to Axis constructor.');
    }
  }

  /**
  * Master render to call all rendering methods
  *
  * @method render
  * @chainable
  */
  render() {
    this.renderAxisX();
    this.renderAxisY();
    this.renderLabels();
    return this;
  }

  /**
  * Render only the x axis
  *
  * @method renderAxisX
  */
  renderAxisX() {
    this.d3Container.selectAll('g.x-axis').remove();
    this.d3Container.append('g')
        .attr('class', 'x-axis')
        .call(axisBottom(this.oScaleX))
        .attr('transform', `translate(${this.jPadding.l},${this.iHeight})`)
        .selectAll('text')
          .attr('x', -5)
          .attr('y', 6)
          .attr('transform', 'rotate(310)')
          .attr('class', 'x-labels')
          .text(d => Utilities.truncateString(d, this.iTruncate))
          .style('text-anchor', 'end')
          .on('mousemove', (d) => {
            if (this.oTooltip && d.length > this.iTruncate) {
              this.oTooltip.ping(`<strong>${d}</strong>`);
            }
          });
  }

  /**
  * Render only the y axis
  *
  * @method renderAxisY
  */
  renderAxisY() {
    this.d3Container.selectAll('g.y-axis').remove();
    this.d3Container.append('g')
        .attr('class', 'y-axis')
        .call(axisLeft(this.oScaleY))
        .attr('transform', `translate(${this.jPadding.l},0)`)
        .selectAll('.y-axis .tick line')
          .attr('x2', () => this.iWidth);
  }

  /**
  * Render only the labels
  *
  * @method renderLabels
  */
  renderLabels() {
    this.d3Container.selectAll('text.labels').remove();
    this.d3Container.append('text')
        .attr('class', 'labels')
        .attr('x', (this.iHeight / -2) - (this.jPadding.t / 2))// - ((this.jConfig.aAxisLabels[0].length / 2) * 7))
        .attr('y', 10)
        .attr('transform', 'rotate(-90)')
        .attr('text-anchor', 'middle')
        .text(this.aAxisLabels[0]);
    this.d3Container.append('text')
        .attr('class', 'labels')
        .attr('x', ((this.iWidth + this.jPadding.l + this.jPadding.r) / 2))// - ((this.aAxisLabels[1].length / 2) * 2))
        .attr('y', this.iHeight + (this.jPadding.b - 5))
        .attr('text-anchor', 'middle')
        .text(this.aAxisLabels[1]);
  }

}

module.exports = Axis;

import { min, max } from 'd3-array';
import { select } from 'd3-selection';
import { scaleLinear, scaleBand } from 'd3-scale';
import Chart from './Chart';
import Key from './Key';
import Axis from './Axis';

/**
* Create AxisChart from the supplied data, based on the supplied JSON config.
*
* @class AxisChart
* @extends {Chart}
* @constructor
*/
export default class AxisChart extends Chart {

  /**
  * The d3 object for the chart's group
  *
  * @property d3ChartGroup
  * @type {Object}
  */
  d3ChartGroup;

  /**
  * The d3 object for the chart's axis
  *
  * @property d3AxisGroup
  * @type {Object}
  */
  d3AxisGroup;

  /**
  * The chart's axis object
  *
  * @property oAxis
  * @type {Object}
  */
  oAxis;

  /**
  * The chart's key object
  *
  * @property oKey
  * @type {Object}
  */
  oKey;

  /**
  * Constructor function supersedes parent class.
  *
  * @method constructor
  * @param {Object} oParams same as Chart
  */
  constructor(oParams = {}) {
    super(oParams);
    this.jPadding = { l: 45, r: 15, t: 25, b: 90 };
    this.oScaleY = scaleLinear();
    this.oScaleX = scaleBand().padding(0.2);
  }

  /**
  * Supersede the parent method to update local scaling objects
  *
  * @method setDimensions
  */
  setDimensions() {
    super.setDimensions();
    const bTrim = this.jConfig.bTrim;
    let iMinValue = bTrim ? min(this.aData, d => min(d.aValues)) : 0;
    let iMaxValue = max(this.aData, d => max(d.aValues));
    const iSection = Math.ceil(iMaxValue / 15);
    if (bTrim) {
      const iLowerSection = iMinValue > iSection ? iMinValue - iSection : 0;
      iMinValue = iMinValue > 0 ? iLowerSection : iMinValue;
      iMinValue = iMinValue < 0 ? iMinValue - iSection : iMinValue;
      iMaxValue += iSection;
    }
    this.oScaleX
      .domain(this.aData.map(d => d.sLabel))
      .range([0, this.iInnerWidth]);
    this.oScaleY
      .domain([iMinValue, iMaxValue])
      .range([this.iInnerHeight, this.jPadding.t]);
    this.iMinValue = iMinValue;
    this.iMaxValue = iMaxValue;
    this.iSection = iSection;
  }

  /**
  * Render the chart including axes and labels
  *
  * @method renderChart
  */
  renderChart() {
    const { aAxisLabels, iTruncate = 15 } = this.jConfig;
    const { iInnerWidth, iInnerHeight, oScaleX, oScaleY, jPadding } = this;

    // Add chart scale axes
    this.d3AxisGroup = this.d3AxisGroup || this.d3Svg.append('g').attr('class', 'axes-g');
    this.oAxis = new Axis({
      d3Container: this.d3AxisGroup,
      iTruncate,
      aAxisLabels,
      oScaleX,
      oScaleY,
      jPadding,
      iWidth: iInnerWidth,
      iHeight: iInnerHeight,
      oTooltip: this.oTooltip }).render();

    // Add chart container group
    this.d3ChartGroup = this.d3ChartGroup || this.d3Svg.append('g').attr('transform', `translate(${this.jPadding.l}, 0)`);

    // Render the key for the data
    this.oKey = new Key({
      d3Container: select(this.dSvg),
      aValues: this.jConfig.aValues,
      iOffsetX: (this.iInnerWidth / 2) + this.jPadding.l,
      iOffsetY: this.iHeight - 20
    }).render();
  }

}

module.exports = AxisChart;

import { easeLinear } from 'd3-ease';
import { select, event } from 'd3-selection';
import 'd3-transition';
import AxisChart from './AxisChart';

/**
* Create BarCharts from the supplied data, based on the JSON config.
*
* @class BarChart
* @extends {AxisChart}
* @constructor
*/
export default class BarChart extends AxisChart {

  /**
  * The local collection of bars
  *
  * @property aBars
  * @type {Array}
  */
  aBars;

  /**
  * Constructor used to set chart type
  *
  * @method constructor
  */
  constructor(oParams = {}) {
    super(oParams);
    this.sChartType = 'bar';
  }

  /**
  * Render the chart including bars, axes and labels
  *
  * @method renderChart
  * @param {Boolean} bReset optionally reset the chart data
  * @param {Boolean} bHeightTransition optionally transition height
  */
  renderChart(bReset = false, bHeightTransition = true) {
    super.renderChart();
    const { aValues, sBarType = 'side' } = this.jConfig;
    const { iInnerHeight, oScaleX, oScaleY } = this;
    const iBarWidth = oScaleX.bandwidth() / aValues.length;

    this.aBars = this.aBars || [];

    // Iterate through config value keys
    aValues.forEach((oValues, i) => {
      const { oColor } = oValues;
      const iBarOffset = sBarType === 'side' ? (iBarWidth * i) : 0;
      // Reset bars data and clear graph
      if (bReset && this.aBars[i]) {
        this.aBars[i] = this.d3ChartGroup.selectAll(`rect.bars-${i}`).data({});
        this.aBars[i].exit().remove();
        this.aBars[i] = undefined;
      }
      // Add bars for each value
      if (!this.aBars[i]) {
        // Bind bars data
        this.aBars[i] = this.d3ChartGroup.selectAll(`rect.bars-${i}`).data(this.aData);
        // Add new rect elements and set base attributes
        this.aBars[i]
          .enter()
          .append('rect')
          .on('mousemove', (d) => {
            this.oTooltip.ping([d.sLabel, oValues.sName, d.aValues[i]]);
          })
          .on('mouseover', (d) => {
            d.oColor = oColor;
            select(event.target).attr('fill', d.oColor.darker(1));
          })
          .on('mousedown', (d) => {
            if (this.jConfig.fnClickCallback) {
              this.jConfig.fnClickCallback({
                oEvent: event,
                jData: d
              });
            }
          })
          .on('mouseout', (d) => {
            this.oTooltip.hide();
            select(event.target).attr('fill', d.oColor);
          })
          .attr('class', `bars bars-${i}`)
          .attr('fill', oColor)
          .attr('y', iInnerHeight)
          .attr('height', 0);
      }

      // Update bars (IIFE used to allow for optional transition)
      (() => {
        const d3BarGroup = this.d3ChartGroup.selectAll(`rect.bars-${i}`);
        d3BarGroup.data(this.aData)
          .attr('x', d => oScaleX(d.sLabel) + iBarOffset)
          .attr('width', iBarWidth);
        if (bHeightTransition) {
          return d3BarGroup.transition(false)
          .ease(easeLinear)
          .duration(this.iTransitionTime);
        }
        return d3BarGroup;
      })().attr('y', (d) => {
        let iValue = d.aValues[i];
        iValue = iValue < 0 ? Math.abs(iValue) : 0;
        return oScaleY(d.aValues[i] + iValue);
      })
      .attr('height', (d) => {
        const iModifier = this.iMinValue < 0 ? Math.abs(this.iMinValue) : 0;
        return iInnerHeight - oScaleY(Math.abs(d.aValues[i]) - iModifier);
      });
    });
  }

}

module.exports = BarChart;

import { forceSimulation, forceManyBody, forceCenter } from 'd3-force';
import { max } from 'd3-array';
import { select } from 'd3-selection';
import { easeLinear } from 'd3-ease';
import { scaleLinear } from 'd3-scale';
import Chart from './Chart';
import Axis from './Axis';
import Key from './Key';

/**
* Create BubbleCharts from the supplied data, based on the supplied JSON config.
*
* @class BubbleChart
* @extends {Chart}
* @constructor
*/
export default class BubbleChart extends Chart {

  /**
  * The local collection of bubbles
  *
  * @property aBubbles
  * @type {Array}
  */
  aBubbles;

  /**
  * Constructor function supersedes parent class.
  *
  * @method constructor
  * @param {Object} oParams same as Chart
  */
  constructor(oParams = {}) {
    super(oParams);
    this.oScaleColor = scaleLinear();
    this.sChartType = 'bubble';
  }

  /**
  * Supersede the parent method to update local scaling objects
  *
  * @method setDimensions
  */
  setDimensions() {
    super.setDimensions();
    this.oScaleColor
      .domain([0, max(this.aData, d => d[this.jConfig.aValues[1].sKey])])
      .range(this.jConfig.aValues[1].aColors);
  }

  /**
  * Render the chart including bubbles, axes and labels
  *
  * @method renderChart
  */
  renderChart() {
    const { aAxisLabels, aValues } = this.jConfig;
    const { iInnerWidth, iInnerHeight, jPadding } = this;
    const { sKey: sKey1, sName: sName1 } = aValues[0];
    const { sKey: sKey2, sName: sName2, aColors } = aValues[1];

    // Add chart scale axes
    this.d3AxisGroup = this.d3AxisGroup || this.d3Svg.append('g').attr('class', 'axes-g');
    this.oAxis = new Axis({
      d3Container: this.d3AxisGroup,
      aAxisLabels,
      jPadding,
      iWidth: iInnerWidth,
      iHeight: iInnerHeight - 25 }).renderLabels();

    // Add the group container for bubbles
    this.d3BubblesGroup = this.d3BubblesGroup || this.d3Svg.append('g').attr('transform', `translate(${this.jPadding.l}, 0)`);

    // The method runs on each tick of the force calculation to reposition the bubbles
    const fnTicked = () => {
      this.d3BubblesGroup.selectAll('circle')
        .attr('cx', d => d.x)
        .attr('cy', d => d.y);
    };

    // Initialise the d3 force calculations
    this.oForce = forceSimulation()
      .nodes(JSON.parse(JSON.stringify(this.aData)))
      .force('charge', forceManyBody().strength(d => (d[sKey1] * (d[sKey1] / 2)) / -150))
      .force('center', forceCenter(this.iWidth / 2, this.iHeight / 2))
      .on('tick', () => {
        fnTicked();
      });

    // Add circles for each value
    if (!this.d3Circles) {
      // Bind bars data
      this.d3Circles = this.d3BubblesGroup.selectAll('circle.circles').data(this.oForce.nodes());
      // Add new rect elements and set base attributes
      this.d3Circles
        .enter()
        .append('circle')
        .on('mousemove', (d) => {
          this.oTooltip.ping(`<strong>${d.sLabel}</strong><br>${sName1}: <em>${d[sKey1]}</em><br>${sName2}: <em>${d[sKey2]}</em>`);
        })
        .on('mouseout', () => this.oTooltip.hide())
        .attr('class', 'circles')
        .attr('fill', d => this.oScaleColor(d[sKey2]))
        .attr('r', 0)
        .transition()
        .ease(easeLinear)
        .duration(this.iTransitionTime)
        .attr('r', d => d[sKey1] / 2);
    } else {
      this.d3BubblesGroup.attr('transform', `translate(${this.iResizeOffset / 2}, 0)`);
    }

    // Render the key for the data
    this.oKey = new Key({
      d3Container: select(this.dSvg),
      aValues: [
        {
          sName: 0,
          sColor: aColors[0]
        },
        {
          sName: this.oScaleColor.domain()[1],
          sColor: aColors[1]
        }
      ],
      iOffsetX: (this.iInnerWidth / 2) + this.jPadding.l + 10,
      iOffsetY: this.iHeight - 20,
      sType: 'range'
    }).render();
  }

}

module.exports = BubbleChart;

import { select } from 'd3-selection';
import Tooltip from './Tooltip';
import DataOps from './DataOps';

/**
* The Chart object is the parent class for all types of Chart.
* It is used to initialise all of the base settings universal to all charts.
*
* @class Chart
* @constructor
*/
export default class Chart {

  /**
  * SVG DOM object for displaying the chart
  *
  * @property dSvg
  * @type {Object}
  */
  dSvg;

  /**
  * SVG d3 object for d3 operations on the chart
  *
  * @property d3Svg
  * @type {Object}
  */
  d3Svg;

  /**
  * Default time for d3 transitions on the chart
  *
  * @property iTransitionTime
  * @type {Number}
  */
  iTransitionTime;

  /**
  * DOM reference to container element that wraps SVG
  *
  * @property dContainer
  * @type {Object}
  */
  dContainer;

  /**
  * DOM reference to loader display element
  *
  * @property dLoader
  * @type {Object}
  */
  dLoader;

  /**
  * Chart's tooltip object
  *
  * @property oTooltip
  * @type {Object}
  */
  oTooltip;

  /**
  * The current calculated width of the chart
  *
  * @property iWidth
  * @type {Number}
  */
  iWidth;

  /**
  * The current calculated height of the chart
  *
  * @property iHeight
  * @type {Number}
  */
  iHeight;

  /**
  * The current calculated inner width of the chart
  *
  * @property iInnerWidth
  * @type {Number}
  */
  iInnerWidth;

  /**
  * The current calculated inner height of the chart
  *
  * @property iInnerHeight
  * @type {Number}
  */
  iInnerHeight;

  /**
  * The width before any browser resize
  *
  * @property iInitialWidth
  * @type {Number}
  */
  iInitialWidth;

  /**
  * The padding for the chart within the container
  *
  * @property jPadding
  * @type {Object}
  */
  jPadding;

  /**
  * The chart's config object
  *
  * @property jConfig
  * @type {Object}
  */
  jConfig;

  /**
  * The chart's data
  *
  * @property aData
  * @type {Array}
  */
  aData;

  /**
  * Constructor function that sets up the local object.
  *
  * @method constructor
  * @param {Object} jConfig JSON configuration object
  * @param {Array} aData the data to be displayed
  * @param {String} sContainer Optional ID to select DOM object
  * @param {Object} dContainer Optional DOM object in place of ID
  */
  constructor(oParams = {}) {
    const { jConfig, aData, sContainer } = oParams;
    let { dContainer } = oParams;
    this.dSvg = document.createElementNS('http://www.w3.org/2000/svg', 'svg');
    this.d3Svg = select(this.dSvg);
    this.iTransitionTime = 500;
    this.jPadding = { l: 5, r: 5, t: 5, b: 5 };
    this.dLoader = document.createElement('div');
    this.dLoader.className = 'dt-loader';
    if (!dContainer && sContainer) {
      dContainer = document.getElementById(sContainer);
    }
    if (dContainer) {
      this.setContainer(dContainer);
    }
    if (jConfig) {
      this.setConfig(jConfig);
    }
    if (aData) {
      this.setData(aData);
    }
  }

  /**
  * Sets the local container object.
  *
  * @method setContainer
  * @param {Object} dContainer Required DOM element
  * @throws {Error} invalid DOM element
  */
  setContainer(dContainer) {
    if (dContainer.nodeName) {
      dContainer.appendChild(this.dLoader);
      this.dContainer = dContainer;
    } else {
      throw new Error('No valid DOM element or ID provided for chart.');
    }
  }

  /**
  * Sets the local config options for the chart.
  *
  * @method setConfig
  * @param {Object} jConfig JSON configuration object
  * @throws {Error} missing configuration
  */
  setConfig(jConfig) {
    if (jConfig && jConfig.toString() === '[object Object]') {
      if (jConfig.aValues) {
        jConfig.aValues = DataOps.addColoursToConfig(jConfig.aValues);
      }
      this.jConfig = jConfig;
    } else {
      throw new Error('No valid configuration provided for chart.');
    }
  }

  /**
  * Sets the local data for the chart.
  *
  * @method setData
  * @param {Array} aData array of JSON objects
  * @param {Boolean} bTransform transform mapped data
  * @throws {Error} missing data
  */
  setData(aData, bTransform = true) {
    if (aData && Array.isArray(aData) === true) {
      if (this.jConfig && bTransform) {
        aData = DataOps.transformDataKeys(this.jConfig, aData);
      }
      this.aData = aData;
    } else {
      throw new Error('No valid data provided for chart.');
    }
  }

  /**
  * Updates the local data for the chart.
  *
  * @method updateData
  * @param {Array} aData array of JSON objects
  * @param {Boolean} bTransform transform mapped data
  */
  updateData(aData, bTransform = true) {
    this.setData(aData, bTransform);
    this.setDimensions();
    if (this.oAxis) {
      this.oAxis.render();
    }
    if (this.renderChart) {
      this.renderChart();
    }
  }

  /**
  * Updates the local config for the chart.
  *
  * @method updateConfig
  * @param {JSON} jConfig config JSON style object
  */
  updateConfig(jConfig) {
    this.setConfig(jConfig);
    if (this.renderChart) {
      this.renderChart(true, false);
    }
  }

  /**
  * Sets the local chart dimensions based on the size of the container.
  *
  * @method setDimensions
  * @throws {Error} missing DOM element
  */
  setDimensions() {
    if (this.dContainer && this.dContainer.nodeName) {
      this.iWidth = this.dContainer.clientWidth;
      this.iHeight = this.dContainer.clientHeight;
      this.iInnerWidth = this.iWidth - this.jPadding.l - this.jPadding.r;
      this.iInnerHeight = this.iHeight - this.jPadding.t - this.jPadding.b;
    } else {
      throw new Error('Cannot set dimensions of chart without container element.');
    }
  }

  /**
  * Check chart is ready and render.
  *
  * @method init
  * @throws {Error} chart not ready for initialisation
  * @chainable
  */
  init() {
    this.setDimensions();
    if (this.aData && this.jConfig && !isNaN(this.iWidth) && !isNaN(this.iHeight)) {
      this.iInitialWidth = this.iWidth;
      this.oTooltip = new Tooltip(this.dContainer).create();
      select(this.dContainer).append('div').attr('class', 'title').text(this.jConfig.sTitle);
      this.dSvg.setAttribute('class', 'chart');
      this.dContainer.appendChild(this.dSvg);
      this.oResizeWatcher = this.oResizeWatcher || window.addEventListener('resize', () => {
        this.setDimensions();
        this.iResizeOffset = this.iWidth - this.iInitialWidth;
        if (this.renderChart) {
          this.renderChart();
        }
        this.oTooltip.hide();
      });
      this.oChartOutWatcher = this.oChartOutWatcher || this.dSvg.addEventListener('mouseout', () => {
        this.oTooltip.hide();
      });
      if (this.renderChart) {
        this.renderChart();
      }
      this.dContainer.removeChild(this.dLoader);
      return this;
    }
    throw new Error('The chart is not ready for initialisation.');
  }

}

module.exports = Chart;

import { rgb } from 'd3-color';
import Utilities from './Utilities';

/**
* The Colors object is is a class containing reusable static methods
*
* @class Colors
* @static
*/
export default class Colors {

  /**
  * Returns a random color (D3 object)
  *
  * @method getRandomColor
  * @static
  * @param {String} sBackground the type of background light/dark to filter for contrast
  * @param {Integer} iBoundary the brightness value to filter
  * @return {Object} randomly generated RGB object
  */
  static getRandomColor(sBackground = 'light', iBoundary = 170) {
    const iRGB = () => Utilities.getRandomInteger(0, 255);
    let aValues = [];
    while (!aValues.length || !this.colorFilter(sBackground, iBoundary, aValues)) {
      aValues = [iRGB(), iRGB(), iRGB()];
    }
    return rgb(aValues[0], aValues[1], aValues[2]);
  }

  /**
  * Used to filter random colour values and ensure high contrast against background
  *
  * @method colorFilter
  * @static
  * @param {String} sBackground the type of background light/dark to filter for contrast
